import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

// Initial State
const state = {
  isDarkMode: false,
};

// Getters
const getters = {
  isDarkMode() {
    return state.isDarkMode;
  },
};

// Mutations
const mutations = {};

export default new Vuex.Store({
  state,
  getters,
  mutations: {},
  actions: {},
  modules: {},
});
