import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

// Initial State
const state = {
  isDarkMode: false,
};

export default new Vuex.Store({
  state: { state },
  getters: {},
  mutations: {},
  actions: {},
  modules: {},
});
